import React, { useState } from "react";
import SignInImage from "../assets/images/SignIn3.jpg";
import { Link } from "react-router-dom";
import axios from "axios";

function SignInComponent() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isSigningIn, setIsSigningIn] = useState(false);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setIsSigningIn(true); // Set signing in state to true to show loading spinner

    try {
      const url = "http://127.0.0.1:8000/sign-in-view/";
      const data = {
        email,
        password,
      };

      const response = await axios.post(url, data);

      // Assuming the server returns a success message or status code upon successful sign-in
      if (response.status === 200) {
        // Redirect the user to the desired page after successful sign-in
        // You can implement this based on your application's logic
        console.log("Sign in successful");
      } else {
        throw new Error("Failed to sign in");
      }
    } catch (error) {
      console.error("Error signing in:", error);
      setIsSigningIn(false); // Reset signing in state
      // Handle error (e.g., display error message to the user)
    }
  };

  return (
    <div className="hero min-h-screen" style={{ backgroundImage: `url(${SignInImage})` }}>
      <div className="hero-content flex-col lg:flex-row-reverse">
        <div className="text-center lg:text-left">
          <h1 className="text-5xl font-bold white-text">Sign In!</h1>
          <p className="py-6 white-text">Let&apos;s get you signed in and back to your journey!</p>
        </div>
        <div className="card shrink-0 w-full max-w-sm shadow-2xl bg-base-100">
          <form className="card-body" onSubmit={handleSubmit}>
            <div className="form-control">
              <label className="label">
                <span className="label-text">Email</span>
              </label>
              <input
                type="email"
                placeholder="Email"
                className="input input-bordered"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            <div className="form-control">
              <label className="label">
                <span className="label-text">Password</span>
              </label>
              <input
                type="password"
                placeholder="Password"
                className="input input-bordered"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
              <label className="label">
                <Link to="/SignUp" className="label-text-alt link link-hover">No Account? Sign Up</Link>
              </label>
            </div>
            <div className="form-control mt-6">
              <button className="btn btn-primary" disabled={isSigningIn}>Sign In</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default SignInComponent;
