import React, { useState, useEffect, useCallback } from "react";
import PropTypes from "prop-types";
import PricingRow from "./PricingRow";
import "./DaisyComponents.css";

function getRandomMentorshipSpots() {
  return Math.floor(Math.random() * 10) + 1;
}

function ProfessionalsSection({ sliceRange }) {
  const [professionals, setProfessionals] = useState([]);

  // Define fetchProfessionals using useCallback to memoize it
  const fetchProfessionals = useCallback(async (newSliceRange) => {
    try {
      const response = await fetch("http://localhost:8000/api/random-users/");
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const data = await response.json();
      setProfessionals(data.slice(newSliceRange[0], newSliceRange[1]));
      console.log('Professionals:', data.slice(newSliceRange[0], newSliceRange[1])); // Log the updated value here
    } catch (error) {
      console.error("Error fetching professionals:", error);
    }
  }, []);

  useEffect(() => {
    fetchProfessionals(sliceRange);
  }, [sliceRange, fetchProfessionals]);

  return (
    <>
      {/* Remove the second dropdown box */}
      {/* <select
        className="select select-primary w-full max-w-xs poop"
        defaultValue=""
        onChange={(event) => {
          const selectedField = event.target.value;
          // Calculate slice range based on the selected field
          let newSliceRange = [0, 10]; // Default to Cyber Security
          switch (selectedField) {
            case "Data Management":
              newSliceRange = [11, 20];
              break;
            case "Software Engineering":
              newSliceRange = [21, 30];
              break;
            case "Business Administration":
              newSliceRange = [31, 40];
              break;
            case "Accounting":
              newSliceRange = [41, 50];
              break;
            default:
              newSliceRange = [0, 10]; // Default to Cyber Security
          }
          fetchProfessionals(newSliceRange);
        }}
      >
        <option value="" disabled>
          Choose Your Field
        </option>
        <option value="Cyber Security">Cyber Security</option>
        <option value="Data Management">Data Management</option>
        <option value="Software Engineering">Software Engineering</option>
        <option value="Business Administration">Business Administration</option>
        <option value="Accounting">Accounting</option>
      </select> */}

      {/* Keep only the first dropdown box */}
      <select
        className="select select-primary w-full max-w-xs"
        defaultValue=""
        onChange={(event) => {
          const selectedField = event.target.value;
          // Calculate slice range based on the selected field
          let newSliceRange = [0, 10]; // Default to Cyber Security
          switch (selectedField) {
            case "Data Management":
              newSliceRange = [11, 20];
              break;
            case "Software Engineering":
              newSliceRange = [21, 30];
              break;
            case "Business Administration":
              newSliceRange = [31, 40];
              break;
            case "Accounting":
              newSliceRange = [41, 50];
              break;
            default:
              newSliceRange = [0, 10]; // Default to Cyber Security
          }
          fetchProfessionals(newSliceRange);
        }}
      >
        <option value="" disabled>
          Choose Your Field
        </option>
        <option value="Cyber Security">Cyber Security</option>
        <option value="Data Management">Data Management</option>
        <option value="Software Engineering">Software Engineering</option>
        <option value="Business Administration">Business Administration</option>
        <option value="Accounting">Accounting</option>
      </select>
    </>
  );
}

ProfessionalsSection.propTypes = {
  sliceRange: PropTypes.array.isRequired,
};

export default ProfessionalsSection;
